package com.logicaldoc.gui.common.client.beans;

import java.io.Serializable;

/**
 * Branding ninformations
 * 
 * @author Marco Meschieri - LogicalDOC
 * @since 7.6.2
 */
public class GUIBranding implements Serializable {

	private static final long serialVersionUID = 1L;

	public static String SRC_PREFIX = "data:image/png;base64,";

	private final static String LOGO_OEM = SRC_PREFIX
			+ "";

	private String logoSrc = SRC_PREFIX
			+ "";

	private String logoMenuSrc = SRC_PREFIX
			+ "";

	private String logoHeadSrc = SRC_PREFIX
			+ "";

	private String faviconSrc = SRC_PREFIX
			+ "";

	private String bannerSrc = SRC_PREFIX
			+ "";

	private String logoOemSrc = LOGO_OEM;

	private String logoHeadOemSrc = LOGO_OEM;

	private String productName = "LogicalDOC Community (free)";

	private String product = "LogicalDOC";

	private String help = "https://docs.logicaldoc.com";

	private String bugs = "https://bugs.logicaldoc.com";

	private String url = "https://www.logicaldoc.com";

	private String forum = "https://forums.logicaldoc.com";

	private String vendor = "LogicalDOC";

	private String vendorAddress = "via Aldo Moro interna, 3";

	private String vendorCap = "41012";

	private String vendorCountry = "Italy";

	private String vendorCity = "Carpi";

	private String support = "support@logicaldoc.com";

	private String sales = "sales@logicaldoc.com";

	private String skin = "Tahoe";

	private String css = null;

	public String getProduct() {
		return product;
	}

	public void setProduct(String product) {
		this.product = product;
	}

	public String getProductName() {
		return productName;
	}

	public void setProductName(String productName) {
		this.productName = productName;
	}

	public String getHelp() {
		return help;
	}

	public void setHelp(String help) {
		this.help = help;
	}

	public String getBugs() {
		return bugs;
	}

	public void setBugs(String bugs) {
		this.bugs = bugs;
	}

	public String getVendor() {
		return vendor;
	}

	public void setVendor(String vendor) {
		this.vendor = vendor;
	}

	public String getUrl() {
		return url;
	}

	public void setUrl(String url) {
		this.url = url;
	}

	public String getVendorAddress() {
		return vendorAddress;
	}

	public void setVendorAddress(String vendorAddress) {
		this.vendorAddress = vendorAddress;
	}

	public String getVendorCap() {
		return vendorCap;
	}

	public void setVendorCap(String vendorCap) {
		this.vendorCap = vendorCap;
	}

	public String getVendorCountry() {
		return vendorCountry;
	}

	public void setVendorCountry(String vendorCountry) {
		this.vendorCountry = vendorCountry;
	}

	public String getVendorCity() {
		return vendorCity;
	}

	public void setVendorCity(String vendorCity) {
		this.vendorCity = vendorCity;
	}

	public String getSupport() {
		return support;
	}

	public void setSupport(String support) {
		this.support = support;
	}

	public String getForum() {
		return forum;
	}

	public void setForum(String forum) {
		this.forum = forum;
	}

	public String getLogoSrc() {
		return logoSrc;
	}

	public void setLogoSrc(String logoSrc) {
		if (logoSrc != null && !logoSrc.startsWith(SRC_PREFIX))
			logoSrc = SRC_PREFIX + logoSrc;
		this.logoSrc = logoSrc;
	}

	public String getLogoMenu() {
		if (logoMenuSrc == null)
			return null;
		return logoMenuSrc.replace(SRC_PREFIX, "");
	}
	
	public String getLogoMenuSrc() {
		return logoMenuSrc;
	}
	
	public void setLogoMenuSrc(String logoMenuSrc) {
		if (logoMenuSrc != null && !logoMenuSrc.startsWith(SRC_PREFIX))
			logoMenuSrc = SRC_PREFIX + logoMenuSrc;
		this.logoMenuSrc = logoMenuSrc;
	}

	public String getLogo() {
		if (logoSrc == null)
			return null;
		return logoSrc.replace(SRC_PREFIX, "");
	}

	public String getLogoHeadSrc() {
		return logoHeadSrc;
	}

	public String getLogoHead() {
		if (logoHeadSrc == null)
			return null;
		return logoHeadSrc.replace(SRC_PREFIX, "");
	}

	public void setLogoHeadSrc(String logoHeadSrc) {
		if (logoHeadSrc != null && !logoHeadSrc.startsWith(SRC_PREFIX))
			logoHeadSrc = SRC_PREFIX + logoHeadSrc;
		this.logoHeadSrc = logoHeadSrc;
	}

	public String getLogoOemSrc() {
		return logoOemSrc;
	}

	public String getLogoOem() {
		if (logoOemSrc == null)
			return null;
		return logoOemSrc.replace(SRC_PREFIX, "");
	}

	public void setLogoOemSrc(String logoOemSrc) {
		if (logoOemSrc != null && !logoOemSrc.startsWith(SRC_PREFIX))
			logoOemSrc = SRC_PREFIX + logoOemSrc;
		this.logoOemSrc = logoOemSrc;
	}

	public boolean isLogoOemCustomized() {
		return !LOGO_OEM.equals(getLogoOemSrc());
	}

	public String getLogoHeadOemSrc() {
		return logoHeadOemSrc;
	}

	public String getLogoHeadOem() {
		if (logoHeadOemSrc == null)
			return null;
		return logoHeadOemSrc.replace(SRC_PREFIX, "");
	}

	public void setLogoHeadOemSrc(String logoHeadOemSrc) {
		if (logoHeadOemSrc != null && !logoHeadOemSrc.startsWith(SRC_PREFIX))
			logoHeadOemSrc = SRC_PREFIX + logoHeadOemSrc;
		this.logoHeadOemSrc = logoHeadOemSrc;
	}

	public String getBannerSrc() {
		return bannerSrc;
	}

	public String getBanner() {
		if (bannerSrc == null)
			return null;
		return bannerSrc.replace(SRC_PREFIX, "");
	}

	public void setBannerSrc(String bannerSrc) {
		if (bannerSrc != null && !bannerSrc.startsWith(SRC_PREFIX))
			bannerSrc = SRC_PREFIX + bannerSrc;
		this.bannerSrc = bannerSrc;
	}

	public String getFaviconSrc() {
		return faviconSrc;
	}

	public void setFaviconSrc(String faviconSrc) {
		if (faviconSrc != null && !faviconSrc.startsWith(SRC_PREFIX))
			faviconSrc = SRC_PREFIX + faviconSrc;
		this.faviconSrc = faviconSrc;
	}

	public String getFavicon() {
		if (faviconSrc == null)
			return null;
		return faviconSrc.replace(SRC_PREFIX, "");
	}

	public String getSales() {
		return sales;
	}

	public void setSales(String sales) {
		this.sales = sales;
	}

	public String getSkin() {
		return skin;
	}

	public void setSkin(String skin) {
		this.skin = skin;
	}

	public String getCss() {
		return css;
	}

	public void setCss(String css) {
		this.css = css;
	}
}